mapper.update:
update transaction
endDate = endDate + #{period}, period = period +#{period}, paymentNo = #{paymentNo}, paymentDate = current_timestamp(), paymentWay = #{paymentWay}, originPrice = originPrice + #{originPrice}, discountPrice = discountPrice + #{discountPrice}, resultPrice = resultPrice + #{resultPrice}
where tranNo = #{tranNo}

mapper.select:
 	SELECT t.tranNo, t.prodImg, t.prodName, t.startDate, t.endDate, t.paymentNo, t. t.userId, t.resultPrice, p.recentImg, p.prodCondition
	FROM product p, transaction t

		p.prodNo = t.prodNo and t.userId = #{userId}


		 and p.prodCondition = #{searchProductCondition}


	ORDER BY t.tranNo DESC
